import React, { useState, useEffect } from 'react';
import { getCandidates, addCandidate, castVote, fetchResults } from './api.js';

function App() {
  const [candidates, setCandidates] = useState([]);
  const [name, setName] = useState('');
  const [results, setResults] = useState([]);

  // Load candidates on component mount
  useEffect(() => {
    loadCandidates();
  }, []);

  const loadCandidates = async () => {
    try {
      const response = await getCandidates();
      setCandidates(response);
    } catch (error) {
      console.error('Error loading candidates:', error);
    }
  };

const handleAddCandidate = async () => {
  if (!name.trim()) {
    alert('Please enter a candidate name');
    return;
  }

  try {
    const response = await addCandidate({ name });
    console.log('Add candidate response:', response);  // Debug log
    setName('');
    loadCandidates();
  } catch (error) {
    console.error('Error adding candidate:', error);  // Debug log
  }
};

  const handleVote = async (id) => {
    try {
      await castVote(id);
      loadCandidates();
    } catch (error) {
      console.error('Error casting vote:', error);
    }
  };

  const handleShowResults = async () => {
    try {
      const response = await fetchResults();
      setResults(response);
    } catch (error) {
      console.error('Error fetching results:', error);
    }
  };

  return (
    <div>
      <h1>Voting App</h1>
      <div>
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Candidate Name"
        />
        <button onClick={handleAddCandidate}>Add Candidate</button>
      </div>

      <h2>Candidates</h2>
      <ul>
        {candidates.map((c) => (
          <li key={c.id}>
            {c.name} - Votes: {c.votes}
            <button onClick={() => handleVote(c.id)}>Vote</button>
          </li>
        ))}
      </ul>

      <button onClick={handleShowResults}>Show Results</button>

      <h2>Results</h2>
      <ul>
        {results.map((c) => (
          <li key={c.id}>
            {c.name} - Votes: {c.votes}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
