import React, { useState, useEffect } from 'react';
import { fetchCandidates, addCandidate, castVote, getResults } from './api';

function App() {
  const [candidates, setCandidates] = useState([]);
  const [name, setName] = useState('');
  const [results, setResults] = useState([]);

  useEffect(() => {
    loadCandidates();
  }, []);

  const loadCandidates = async () => {
    const response = await fetchCandidates();
    setCandidates(response.data);
  };

  const handleAddCandidate = async () => {
    await addCandidate(name);
    setName('');
    loadCandidates();
  };

  const handleVote = async (id) => {
    await castVote(id);
    loadCandidates();
  };

  const handleGetResults = async () => {
    const response = await getResults();
    setResults(response.data);
  };

  return (
    <div>
      <h1>Voting App</h1>
      <input 
        value={name} 
        onChange={(e) => setName(e.target.value)} 
        placeholder="Candidate Name" 
      />
      <button onClick={handleAddCandidate}>Add Candidate</button>
      <h2>Candidates</h2>
      <ul>
        {candidates.map((c) => (
          <li key={c.id}>
            {c.name} - Votes: {c.votes} 
            <button onClick={() => handleVote(c.id)}>Vote</button>
          </li>
        ))}
      </ul>
      <button onClick={handleGetResults}>Show Results</button>
      <h2>Results</h2>
      <ul>
        {results.map((r) => (
          <li key={r.id}>
            {r.name} - Votes: {r.votes}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
